1/** Mission Control UI components */
2export { default as StatusBar } from './StatusBar';
3export { default as ProjectSidebar } from './ProjectSidebar';
4export { default as AgentRunCard } from './AgentRunCard';
5export { default as EventLog } from './EventLog';
6export { default as FloatingActionButton } from './FloatingActionButton';
7export { default as NewIssueModal } from './NewIssueModal';
8export { ProjectSetup } from './ProjectSetup';
9

  1import { useState, useCallback, useMemo } from 'react';
  2import type { ViewMode, Project } from './types';
  3import useMissionControl from './hooks/useMissionControl';
  4import { WebSocketProvider } from './contexts/WebSocketContext';
  5import StatusBar from './components/StatusBar';
  6import ProjectSidebar from './components/ProjectSidebar';
  7import AgentRunCard from './components/AgentRunCard';
  8import EventLog from './components/EventLog';
  9import FloatingActionButton from './components/FloatingActionButton';
 10import NewIssueModal from './components/NewIssueModal';
 11import { ProjectSetup } from './components/ProjectSetup';
 12import { api } from './api/client';
 13
 14/**
 15 * Mission Control dashboard  the main application shell.
 16 * Aggregates all projects and agent runs into a unified monitoring view.
 17 * Layout: StatusBar (top) -> flex row [ProjectSidebar | Agent Grid] -> EventLog (bottom).
 18 */
 19function MissionControl() {
 20  const {
 21    projects,
 22    agentRunCards,
 23    statusCounts,
 24    eventLog,
 25    phases,
 26    agentTeams,
 27    agentEvents,
 28    loading,
 29    selectedProjectId,
 30    setSelectedProjectId,
 31    triggerPipeline,
 32    cancelPipeline,
 33    createIssue,
 34    createProject,
 35    deleteProject,
 36    issuesByProject,
 37  } = useMissionControl();
 38
 39  const [viewMode, setViewMode] = useState<ViewMode>('grid');
 40  const [showNewIssueModal, setShowNewIssueModal] = useState(false);
 41  const [newIssueProjectId, setNewIssueProjectId] = useState<number | null>(null);
 42  const [showProjectSetup, setShowProjectSetup] = useState(false);
 43
 44  /** Compute run counts by project for the sidebar */
 45  const runsByProject = useMemo(() => {
 46    const map = new Map<number, { running: number; total: number }>();
 47    for (const card of agentRunCards) {
 48      const pid = card.project.id;
 49      const existing = map.get(pid) ?? { running: 0, total: 0 };
 50      existing.total++;
 51      if (card.run.status === 'running') existing.running++;
 52      map.set(pid, existing);
 53    }
 54    return map;
 55  }, [agentRunCards]);
 56
 57  /** Projects filtered by the current sidebar selection */
 58  const displayedProjects = useMemo(() => {
 59    if (selectedProjectId === null) return projects;
 60    return projects.filter(p => p.id === selectedProjectId);
 61  }, [projects, selectedProjectId]);
 62
 63  const handleNewIssue = useCallback(() => {
 64    setNewIssueProjectId(null);
 65    setShowNewIssueModal(true);
 66  }, []);
 67
 68  const handleNewProject = useCallback(() => {
 69    setShowProjectSetup(true);
 70  }, []);
 71
 72  const handleSyncGithub = useCallback(async () => {
 73    const project = projects.find(p =>
 74      selectedProjectId === null ? true : p.id === selectedProjectId
 75    );
 76    if (project) {
 77      try {
 78        await api.syncGithub(project.id);
 79      } catch (err) {
 80        console.error('Sync failed:', err);
 81      }
 82    }
 83  }, [projects, selectedProjectId]);
 84
 85  const handleIssueSubmit = useCallback(async (projectId: number, title: string, description: string) => {
 86    const issue = await createIssue(projectId, title, description);
 87    await triggerPipeline(issue.id);
 88  }, [createIssue, triggerPipeline]);
 89
 90  const handleProjectSelect = useCallback((project: Project) => {
 91    setSelectedProjectId(project.id);
 92    setShowProjectSetup(false);
 93  }, [setSelectedProjectId]);
 94
 95  const handleProjectCreate = useCallback((name: string, path: string) => {
 96    createProject(name, path).then(() => {
 97      setShowProjectSetup(false);
 98    }).catch(console.error);
 99  }, [createProject]);
100
101  const handleCloneProject = useCallback(async (repoUrl: string) => {
102    const project = await api.cloneProject(repoUrl);
103    setShowProjectSetup(false);
104    setSelectedProjectId(project.id);
105  }, [setSelectedProjectId]);
106
107  const handleDeleteProject = useCallback(async (projectId: number) => {
108    await deleteProject(projectId);
109    if (selectedProjectId === projectId) {
110      setSelectedProjectId(null);
111    }
112  }, [deleteProject, selectedProjectId, setSelectedProjectId]);
113
114  // Loading state
115  if (loading) {
116    return (
117      <div style={{
118        display: 'flex',
119        alignItems: 'center',
120        justifyContent: 'center',
121        height: '100vh',
122        backgroundColor: 'var(--color-bg-primary)',
123        color: 'var(--color-text-primary)',
124        gap: '12px',
125        fontSize: '14px',
126      }}>
127        <span className="pulse-dot" style={{
128          width: '8px',
129          height: '8px',
130          borderRadius: '50%',
131          backgroundColor: 'var(--color-success)',
132        }} />
133        Initializing Mission Control...
134      </div>
135    );
136  }
137
138  // No projects  show ProjectSetup full-screen
139  if (projects.length === 0) {
140    return (
141      <div style={{
142        height: '100vh',
143        backgroundColor: 'var(--color-bg-primary)',
144      }}>
145        <ProjectSetup
146          projects={[]}
147          onSelect={handleProjectSelect}
148          onCreate={handleProjectCreate}
149          onClone={handleCloneProject}
150        />
151      </div>
152    );
153  }
154
155  return (
156    <div style={{
157      display: 'flex',
158      flexDirection: 'column',
159      height: '100vh',
160      backgroundColor: 'var(--color-bg-primary)',
161      color: 'var(--color-text-primary)',
162    }}>
163      {/* Top bar */}
164      <StatusBar
165        agentCounts={{
166          running: statusCounts.running,
167          queued: statusCounts.queued,
168          completed: statusCounts.completed,
169          failed: statusCounts.failed,
170        }}
171        projectCount={projects.length}
172        viewMode={viewMode}
173        onViewModeChange={setViewMode}
174      />
175
176      {/* Main content: sidebar + agent grid */}
177      <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
178        <ProjectSidebar
179          projects={projects}
180          selectedProjectId={selectedProjectId}
181          onSelectProject={setSelectedProjectId}
182          onDeleteProject={handleDeleteProject}
183          runsByProject={runsByProject}
184        />
185
186        {/* Agent run grid */}
187        <div style={{
188          flex: 1,
189          overflowY: 'auto',
190          padding: '16px',
191        }}>
192          {agentRunCards.length === 0 ? (
193            <div style={{
194              display: viewMode === 'grid' ? 'grid' : 'flex',
195              gridTemplateColumns: viewMode === 'grid' ? 'repeat(auto-fill, minmax(400px, 1fr))' : undefined,
196              flexDirection: viewMode === 'list' ? 'column' : undefined,
197              gap: '12px',
198            }}>
199              {displayedProjects.map(project => {
200                const issueCount = issuesByProject.get(project.id) ?? 0;
201                return (
202                  <div
203                    key={project.id}
204                    style={{
205                      backgroundColor: 'var(--color-bg-card)',
206                      border: '1px solid var(--color-border)',
207                      borderLeft: '3px solid var(--color-text-secondary)',
208                    }}
209                  >
210                    <div style={{
211                      display: 'flex',
212                      alignItems: 'center',
213                      padding: '12px',
214                      gap: '12px',
215                    }}>
216                      {/* Idle dot */}
217                      <span style={{
218                        width: '8px',
219                        height: '8px',
220                        borderRadius: '50%',
221                        backgroundColor: 'var(--color-text-secondary)',
222                        flexShrink: 0,
223                      }} />
224
225                      {/* Project name */}
226                      <div style={{ flex: 1, minWidth: 0 }}>
227                        <div style={{
228                          fontSize: '13px',
229                          fontWeight: 600,
230                          overflow: 'hidden',
231                          textOverflow: 'ellipsis',
232                          whiteSpace: 'nowrap',
233                        }}>
234                          {project.name}
235                        </div>
236                        <div style={{
237                          fontSize: '11px',
238                          color: 'var(--color-text-secondary)',
239                          marginTop: '2px',
240                        }}>
241                          {issueCount} {issueCount === 1 ? 'issue' : 'issues'}
242                        </div>
243                      </div>
244
245                      {/* Status label */}
246                      <span style={{
247                        fontSize: '11px',
248                        color: 'var(--color-text-secondary)',
249                        fontWeight: 600,
250                        flexShrink: 0,
251                      }}>
252                        IDLE
253                      </span>
254
255                      {/* Create Issue button */}
256                      <button
257                        onClick={() => {
258                          setNewIssueProjectId(project.id);
259                          setShowNewIssueModal(true);
260                        }}
261                        style={{
262                          padding: '4px 10px',
263                          fontSize: '11px',
264                          fontFamily: 'inherit',
265                          background: 'transparent',
266                          border: '1px solid var(--color-border)',
267                          color: 'var(--color-success)',
268                          cursor: 'pointer',
269                          flexShrink: 0,
270                        }}
271                        onMouseEnter={e => (e.currentTarget.style.backgroundColor = 'var(--color-bg-card-hover)')}
272                        onMouseLeave={e => (e.currentTarget.style.backgroundColor = 'transparent')}
273                      >
274                        + Issue
275                      </button>
276                    </div>
277                  </div>
278                );
279              })}
280            </div>
281          ) : (
282            <div style={{
283              display: viewMode === 'grid' ? 'grid' : 'flex',
284              gridTemplateColumns: viewMode === 'grid' ? 'repeat(auto-fill, minmax(400px, 1fr))' : undefined,
285              flexDirection: viewMode === 'list' ? 'column' : undefined,
286              gap: '12px',
287            }}>
288              {agentRunCards.map(card => (
289                <AgentRunCard
290                  key={card.run.id}
291                  card={card}
292                  phases={phases.get(card.run.id)}
293                  agentTeam={agentTeams.get(card.run.id)}
294                  agentEvents={agentEvents}
295                  onCancel={cancelPipeline}
296                  viewMode={viewMode}
297                />
298              ))}
299            </div>
300          )}
301        </div>
302      </div>
303
304      {/* Bottom event log */}
305      <EventLog entries={eventLog} />
306
307      {/* Floating action button */}
308      <FloatingActionButton
309        onNewIssue={handleNewIssue}
310        onNewProject={handleNewProject}
311        onSyncGithub={handleSyncGithub}
312      />
313
314      {/* New Issue Modal */}
315      {showNewIssueModal && (
316        <NewIssueModal
317          projects={projects}
318          defaultProjectId={newIssueProjectId}
319          onSubmit={handleIssueSubmit}
320          onClose={() => { setShowNewIssueModal(false); setNewIssueProjectId(null); }}
321        />
322      )}
323
324      {/* Project Setup Modal */}
325      {showProjectSetup && (
326        <div
327          data-testid="project-setup-modal"
328          onClick={() => setShowProjectSetup(false)}
329          style={{
330            position: 'fixed',
331            inset: 0,
332            backgroundColor: 'rgba(0,0,0,0.7)',
333            display: 'flex',
334            alignItems: 'center',
335            justifyContent: 'center',
336            zIndex: 100,
337          }}
338        >
339          <div onClick={e => e.stopPropagation()} style={{ width: '100%', maxWidth: '500px' }}>
340            <ProjectSetup
341              projects={projects}
342              onSelect={handleProjectSelect}
343              onCreate={handleProjectCreate}
344              onClone={handleCloneProject}
345            />
346          </div>
347        </div>
348      )}
349    </div>
350  );
351}
352
353function App() {
354  const wsUrl = `${window.location.protocol === 'https:' ? 'wss:' : 'ws:'}//${window.location.host}/ws`;
355  return (
356    <WebSocketProvider url={wsUrl}>
357      <MissionControl />
358    </WebSocketProvider>
359  );
360}
361
362export default App;
