  1/** Unified state hook for the Mission Control dashboard  aggregates all projects into a single view. */
  2import { useState, useEffect, useCallback, useRef, useMemo } from 'react';
  3import { api } from '../api/client';
  4import { useWsSubscribe } from '../contexts/WebSocketContext';
  5import type {
  6  Project,
  7  Issue,
  8  PipelineRun,
  9  PipelinePhase,
 10  AgentRunCard,
 11  EventLogEntry,
 12  WsMessage,
 13  RunStatusFilter,
 14  AgentTeamDetail,
 15  AgentEvent,
 16} from '../types';
 17
 18/** Internal state shape for the Mission Control hook */
 19interface MissionControlState {
 20  projects: Project[];
 21  runs: Map<number, PipelineRun>;
 22  issues: Map<number, Issue>;
 23  phases: Map<number, PipelinePhase[]>;
 24  agentTeams: Map<number, AgentTeamDetail>;
 25  agentEvents: Map<number, AgentEvent[]>;
 26  eventLog: EventLogEntry[];
 27  loading: boolean;
 28  error: string | null;
 29}
 30
 31/** Return type of the useMissionControl hook */
 32export interface MissionControlReturn {
 33  /** All loaded projects */
 34  projects: Project[];
 35  /** Filtered and sorted agent run cards for the grid */
 36  agentRunCards: AgentRunCard[];
 37  /** Run counts by status */
 38  statusCounts: Record<RunStatusFilter, number>;
 39  /** Chronological event log entries */
 40  eventLog: EventLogEntry[];
 41  /** Pipeline phases by run ID */
 42  phases: Map<number, PipelinePhase[]>;
 43  /** Agent team details by run ID */
 44  agentTeams: Map<number, AgentTeamDetail>;
 45  /** Agent events by task ID */
 46  agentEvents: Map<number, AgentEvent[]>;
 47  /** Whether initial data is still loading */
 48  loading: boolean;
 49  /** Error message from initial load, or null */
 50  error: string | null;
 51  /** Currently selected project ID filter, or null for all */
 52  selectedProjectId: number | null;
 53  /** Set the project filter */
 54  setSelectedProjectId: (id: number | null) => void;
 55  /** Current status filter */
 56  statusFilter: RunStatusFilter;
 57  /** Set the status filter */
 58  setStatusFilter: (filter: RunStatusFilter) => void;
 59  /** Trigger a pipeline run for an issue */
 60  triggerPipeline: (issueId: number) => Promise<void>;
 61  /** Cancel a running pipeline */
 62  cancelPipeline: (runId: number) => Promise<void>;
 63  /** Create a new issue in a project */
 64  createIssue: (projectId: number, title: string, description: string) => Promise<Issue>;
 65  /** Create a new project */
 66  createProject: (name: string, path: string) => Promise<Project>;
 67  /** Delete a project and all its data */
 68  deleteProject: (projectId: number) => Promise<void>;
 69  /** Refresh all data from the server */
 70  refresh: () => Promise<void>;
 71  /** Issue counts grouped by project ID */
 72  issuesByProject: Map<number, number>;
 73}
 74
 75/** Status sort order: running first, then queued, failed, completed, cancelled */
 76const STATUS_ORDER: Record<string, number> = {
 77  running: 0,
 78  queued: 1,
 79  failed: 2,
 80  completed: 3,
 81  cancelled: 4,
 82};
 83
 84/**
 85 * Primary data hook for the Mission Control dashboard.
 86 * Aggregates data from all projects, handles WebSocket updates,
 87 * and provides filtering and actions.
 88 */
 89export default function useMissionControl(): MissionControlReturn {
 90  const [state, setState] = useState<MissionControlState>({
 91    projects: [],
 92    runs: new Map(),
 93    issues: new Map(),
 94    phases: new Map(),
 95    agentTeams: new Map(),
 96    agentEvents: new Map(),
 97    eventLog: [],
 98    loading: true,
 99    error: null,
100  });
101
102  const [selectedProjectId, setSelectedProjectId] = useState<number | null>(null);
103  const [statusFilter, setStatusFilter] = useState<RunStatusFilter>('all');
104  const eventIdRef = useRef(0);
105  const mountedRef = useRef(true);
106
107  /** Add an event log entry, capping at 500 entries */
108  const addLogEntry = useCallback((
109    source: EventLogEntry['source'],
110    message: string,
111    projectName?: string,
112    runId?: number,
113  ) => {
114    setState(prev => ({
115      ...prev,
116      eventLog: [
117        ...prev.eventLog.slice(-499),
118        {
119          id: String(++eventIdRef.current),
120          timestamp: new Date().toISOString(),
121          source,
122          message,
123          projectName,
124          runId,
125        },
126      ],
127    }));
128  }, []);
129
130  // Load all projects and their active runs on mount
131  useEffect(() => {
132    mountedRef.current = true;
133    let cancelled = false;
134
135    async function loadAll() {
136      try {
137        const projects = await api.listProjects();
138        if (cancelled) return;
139
140        const issueMap = new Map<number, Issue>();
141        const runMap = new Map<number, PipelineRun>();
142        const phaseMap = new Map<number, PipelinePhase[]>();
143
144        for (const project of projects) {
145          try {
146            const board = await api.getBoard(project.id);
147            if (cancelled) return;
148            for (const col of board.columns) {
149              for (const item of col.issues) {
150                issueMap.set(item.issue.id, item.issue);
151                if (item.active_run) {
152                  runMap.set(item.active_run.id, item.active_run);
153                }
154              }
155            }
156          } catch {
157            // Skip projects that fail to load
158          }
159        }
160
161        if (!cancelled) {
162          setState(prev => ({
163            ...prev,
164            projects,
165            issues: issueMap,
166            runs: runMap,
167            phases: phaseMap,
168            loading: false,
169          }));
170          addLogEntry('system', `Loaded ${projects.length} projects, ${runMap.size} active runs`);
171        }
172      } catch (err) {
173        if (!cancelled) {
174          setState(prev => ({
175            ...prev,
176            loading: false,
177            error: err instanceof Error ? err.message : 'Failed to load',
178          }));
179        }
180      }
181    }
182
183    loadAll();
184    return () => { cancelled = true; mountedRef.current = false; };
185  }, [addLogEntry]);
186
187  // WebSocket message handler  uses a ref for issues to avoid stale closure
188  const issuesRef = useRef(state.issues);
189  issuesRef.current = state.issues;
190
191  useWsSubscribe(useCallback((msg: WsMessage) => {
192    if (!mountedRef.current) return;
193
194    switch (msg.type) {
195      case 'PipelineStarted': {
196        const run = msg.data.run;
197        setState(prev => {
198          const newRuns = new Map(prev.runs);
199          newRuns.set(run.id, run);
200          return { ...prev, runs: newRuns };
201        });
202        const issue = issuesRef.current.get(run.issue_id);
203        addLogEntry('system', `Pipeline started for "${issue?.title ?? `issue #${run.issue_id}`}"`, undefined, run.id);
204        break;
205      }
206
207      case 'PipelineProgress': {
208        const { run_id, phase, iteration } = msg.data;
209        setState(prev => {
210          const newRuns = new Map(prev.runs);
211          const existing = newRuns.get(run_id);
212          if (existing) {
213            newRuns.set(run_id, { ...existing, current_phase: phase, iteration });
214          }
215          return { ...prev, runs: newRuns };
216        });
217        break;
218      }
219
220      case 'PipelineCompleted': {
221        const run = msg.data.run;
222        setState(prev => {
223          const newRuns = new Map(prev.runs);
224          newRuns.set(run.id, run);
225          return { ...prev, runs: newRuns };
226        });
227        addLogEntry('system', 'Pipeline completed successfully', undefined, run.id);
228        break;
229      }
230
231      case 'PipelineFailed': {
232        const run = msg.data.run;
233        setState(prev => {
234          const newRuns = new Map(prev.runs);
235          newRuns.set(run.id, run);
236          return { ...prev, runs: newRuns };
237        });
238        addLogEntry('error', `Pipeline failed: ${run.error ?? 'unknown error'}`, undefined, run.id);
239        break;
240      }
241
242      case 'PipelinePhaseStarted': {
243        const { run_id, phase_number, phase_name } = msg.data;
244        setState(prev => {
245          const newPhases = new Map(prev.phases);
246          const existing = newPhases.get(run_id) ?? [];
247          const newPhase: PipelinePhase = {
248            id: 0,
249            run_id,
250            phase_number,
251            phase_name,
252            status: 'running',
253            iteration: null,
254            budget: null,
255            started_at: new Date().toISOString(),
256            completed_at: null,
257            error: null,
258          };
259          newPhases.set(run_id, [...existing, newPhase]);
260          return { ...prev, phases: newPhases };
261        });
262        addLogEntry('phase', `Phase "${phase_name}" started`, undefined, run_id);
263        break;
264      }
265
266      case 'PipelinePhaseCompleted': {
267        const { run_id, phase_number, success } = msg.data;
268        setState(prev => {
269          const newPhases = new Map(prev.phases);
270          const existing = newPhases.get(run_id) ?? [];
271          newPhases.set(run_id, existing.map(p =>
272            p.phase_number === phase_number
273              ? { ...p, status: success ? 'completed' : 'failed', completed_at: new Date().toISOString() }
274              : p
275          ));
276          return { ...prev, phases: newPhases };
277        });
278        addLogEntry('phase', `Phase completed (${success ? 'success' : 'failed'})`, undefined, run_id);
279        break;
280      }
281
282      case 'PipelineReviewStarted': {
283        addLogEntry('review', 'Review started', undefined, msg.data.run_id);
284        break;
285      }
286
287      case 'PipelineReviewCompleted': {
288        const { passed, findings_count } = msg.data;
289        addLogEntry('review', `Review ${passed ? 'passed' : `failed (${findings_count} findings)`}`, undefined, msg.data.run_id);
290        break;
291      }
292
293      case 'PipelineBranchCreated': {
294        const { run_id, branch_name } = msg.data;
295        setState(prev => {
296          const newRuns = new Map(prev.runs);
297          const existing = newRuns.get(run_id);
298          if (existing) {
299            newRuns.set(run_id, { ...existing, branch_name });
300          }
301          return { ...prev, runs: newRuns };
302        });
303        addLogEntry('git', `Branch created: ${branch_name}`, undefined, run_id);
304        break;
305      }
306
307      case 'PipelinePrCreated': {
308        const { run_id, pr_url } = msg.data;
309        setState(prev => {
310          const newRuns = new Map(prev.runs);
311          const existing = newRuns.get(run_id);
312          if (existing) {
313            newRuns.set(run_id, { ...existing, pr_url });
314          }
315          return { ...prev, runs: newRuns };
316        });
317        addLogEntry('git', 'PR created', undefined, run_id);
318        break;
319      }
320
321      case 'TeamCreated': {
322        const { run_id, team_id, strategy, isolation, plan_summary, tasks } = msg.data;
323        setState(prev => {
324          const newTeams = new Map(prev.agentTeams);
325          newTeams.set(run_id, {
326            team: { id: team_id, run_id, strategy, isolation, plan_summary, created_at: new Date().toISOString() },
327            tasks: tasks ?? [],
328          });
329          return { ...prev, agentTeams: newTeams };
330        });
331        addLogEntry('agent', `Agent team created (${strategy})`, undefined, run_id);
332        break;
333      }
334
335      case 'AgentTaskStarted': {
336        addLogEntry('agent', `Task "${msg.data.name}" started (${msg.data.role})`, undefined, undefined);
337        break;
338      }
339
340      case 'AgentThinking':
341      case 'AgentAction':
342      case 'AgentOutput':
343      case 'AgentSignal': {
344        const taskId = msg.data.task_id;
345        setState(prev => {
346          const newEvents = new Map(prev.agentEvents);
347          const taskEvents = newEvents.get(taskId) ?? [];
348          const event: AgentEvent = {
349            id: taskEvents.length + 1,
350            task_id: taskId,
351            event_type: msg.type === 'AgentThinking' ? 'thinking'
352              : msg.type === 'AgentAction' ? 'action'
353              : msg.type === 'AgentOutput' ? 'output'
354              : 'signal',
355            content: msg.type === 'AgentAction' ? msg.data.summary : msg.data.content,
356            metadata: msg.type === 'AgentAction' ? msg.data.metadata : null,
357            created_at: new Date().toISOString(),
358          };
359          newEvents.set(taskId, [...taskEvents.slice(-199), event]);
360          return { ...prev, agentEvents: newEvents };
361        });
362        break;
363      }
364
365      case 'IssueCreated': {
366        const issue = msg.data.issue;
367        setState(prev => {
368          const newIssues = new Map(prev.issues);
369          newIssues.set(issue.id, issue);
370          return { ...prev, issues: newIssues };
371        });
372        addLogEntry('system', `Issue created: "${issue.title}"`);
373        break;
374      }
375
376      case 'IssueDeleted': {
377        setState(prev => {
378          const newIssues = new Map(prev.issues);
379          newIssues.delete(msg.data.issue_id);
380          return { ...prev, issues: newIssues };
381        });
382        break;
383      }
384
385      case 'ProjectDeleted': {
386        const deletedId = msg.data.project_id;
387        setState(prev => {
388          const newProjects = prev.projects.filter(p => p.id !== deletedId);
389          const newIssues = new Map(prev.issues);
390          const newRuns = new Map(prev.runs);
391          // Remove issues belonging to the deleted project
392          for (const [id, issue] of newIssues) {
393            if (issue.project_id === deletedId) {
394              // Also remove runs for this issue
395              for (const [runId, run] of newRuns) {
396                if (run.issue_id === id) newRuns.delete(runId);
397              }
398              newIssues.delete(id);
399            }
400          }
401          return { ...prev, projects: newProjects, issues: newIssues, runs: newRuns };
402        });
403        addLogEntry('system', 'Project deleted');
404        break;
405      }
406    }
407  }, [addLogEntry]));
408
409  // Compute filtered and sorted agent run cards
410  const agentRunCards: AgentRunCard[] = useMemo(() => {
411    return Array.from(state.runs.values())
412      .filter(run => {
413        if (selectedProjectId !== null) {
414          const issue = state.issues.get(run.issue_id);
415          if (issue && issue.project_id !== selectedProjectId) return false;
416        }
417        if (statusFilter !== 'all' && run.status !== statusFilter) return false;
418        return true;
419      })
420      .sort((a, b) => {
421        const diff = (STATUS_ORDER[a.status] ?? 5) - (STATUS_ORDER[b.status] ?? 5);
422        if (diff !== 0) return diff;
423        return new Date(b.started_at).getTime() - new Date(a.started_at).getTime();
424      })
425      .map(run => ({
426        run,
427        issue: state.issues.get(run.issue_id) ?? {
428          id: run.issue_id,
429          project_id: 0,
430          title: `Issue #${run.issue_id}`,
431          description: '',
432          column: 'backlog' as const,
433          position: 0,
434          priority: 'medium' as const,
435          labels: [],
436          github_issue_number: null,
437          created_at: '',
438          updated_at: '',
439        },
440        project: state.projects.find(p => {
441          const issue = state.issues.get(run.issue_id);
442          return issue && p.id === issue.project_id;
443        }) ?? { id: 0, name: 'Unknown', path: '', github_repo: null, created_at: '' },
444      }));
445  }, [state.runs, state.issues, state.projects, selectedProjectId, statusFilter]);
446
447  // Compute status counts (unfiltered  always reflect total state)
448  const statusCounts: Record<RunStatusFilter, number> = useMemo(() => {
449    const runs = Array.from(state.runs.values());
450    return {
451      all: runs.length,
452      running: runs.filter(r => r.status === 'running').length,
453      queued: runs.filter(r => r.status === 'queued').length,
454      completed: runs.filter(r => r.status === 'completed').length,
455      failed: runs.filter(r => r.status === 'failed').length,
456    };
457  }, [state.runs]);
458
459  // Actions
460  const triggerPipeline = useCallback(async (issueId: number) => {
461    const run = await api.triggerPipeline(issueId);
462    setState(prev => {
463      const newRuns = new Map(prev.runs);
464      newRuns.set(run.id, run);
465      return { ...prev, runs: newRuns };
466    });
467  }, []);
468
469  const cancelPipeline = useCallback(async (runId: number) => {
470    const run = await api.cancelPipelineRun(runId);
471    setState(prev => {
472      const newRuns = new Map(prev.runs);
473      newRuns.set(run.id, run);
474      return { ...prev, runs: newRuns };
475    });
476  }, []);
477
478  const createIssue = useCallback(async (projectId: number, title: string, description: string) => {
479    const issue = await api.createIssue(projectId, title, description);
480    setState(prev => {
481      const newIssues = new Map(prev.issues);
482      newIssues.set(issue.id, issue);
483      return { ...prev, issues: newIssues };
484    });
485    return issue;
486  }, []);
487
488  const createProject = useCallback(async (name: string, path: string) => {
489    const project = await api.createProject(name, path);
490    setState(prev => ({
491      ...prev,
492      projects: [...prev.projects, project],
493    }));
494    return project;
495  }, []);
496
497  const deleteProject = useCallback(async (projectId: number) => {
498    await api.deleteProject(projectId);
499    // Optimistic removal  WS message will also trigger state cleanup
500    setState(prev => ({
501      ...prev,
502      projects: prev.projects.filter(p => p.id !== projectId),
503    }));
504  }, []);
505
506  const refresh = useCallback(async () => {
507    setState(prev => ({ ...prev, loading: true }));
508    try {
509      const projects = await api.listProjects();
510      const issueMap = new Map<number, Issue>();
511      const runMap = new Map<number, PipelineRun>();
512      for (const project of projects) {
513        try {
514          const board = await api.getBoard(project.id);
515          for (const col of board.columns) {
516            for (const item of col.issues) {
517              issueMap.set(item.issue.id, item.issue);
518              if (item.active_run) {
519                runMap.set(item.active_run.id, item.active_run);
520              }
521            }
522          }
523        } catch { /* skip */ }
524      }
525      setState(prev => ({
526        ...prev,
527        projects,
528        issues: issueMap,
529        runs: runMap,
530        loading: false,
531        error: null,
532      }));
533    } catch (err) {
534      setState(prev => ({
535        ...prev,
536        loading: false,
537        error: err instanceof Error ? err.message : 'Refresh failed',
538      }));
539    }
540  }, []);
541
542  // Compute issue counts grouped by project ID
543  const issuesByProject = useMemo(() => {
544    const map = new Map<number, number>();
545    for (const issue of state.issues.values()) {
546      map.set(issue.project_id, (map.get(issue.project_id) ?? 0) + 1);
547    }
548    return map;
549  }, [state.issues]);
550
551  return {
552    projects: state.projects,
553    agentRunCards,
554    statusCounts,
555    eventLog: state.eventLog,
556    phases: state.phases,
557    agentTeams: state.agentTeams,
558    agentEvents: state.agentEvents,
559    loading: state.loading,
560    error: state.error,
561    selectedProjectId,
562    setSelectedProjectId,
563    statusFilter,
564    setStatusFilter,
565    triggerPipeline,
566    cancelPipeline,
567    createIssue,
568    createProject,
569    deleteProject,
570    refresh,
571    issuesByProject,
572  };
573}
