  1/** Expandable agent run card  collapsed summary with status, expanded tabbed detail view. */
  2import { useState, useEffect, useRef } from 'react';
  3import type { AgentRunCard as AgentRunCardType, PipelinePhase, AgentTeamDetail, AgentEvent, PipelineStatus, AgentEventType } from '../types';
  4
  5/** Tab options for the expanded detail view. */
  6export type DetailTab = 'output' | 'phases' | 'files';
  7
  8/** Props for the AgentRunCard component. */
  9export interface AgentRunCardProps {
 10  /** Combined issue + run + project data for the card. */
 11  card: AgentRunCardType;
 12  /** Pipeline phases for the phases tab. */
 13  phases?: PipelinePhase[];
 14  /** Agent team detail with tasks. */
 15  agentTeam?: AgentTeamDetail;
 16  /** Agent events keyed by task ID. */
 17  agentEvents?: Map<number, AgentEvent[]>;
 18  /** Callback when the cancel button is clicked. */
 19  onCancel?: (runId: number) => void;
 20  /** Grid or list layout mode. */
 21  viewMode: 'grid' | 'list';
 22}
 23
 24/** Status dot color mapped to CSS custom properties. */
 25export const STATUS_DOT_COLORS: Record<PipelineStatus, string> = {
 26  running: 'var(--color-success)',
 27  queued: 'var(--color-warning)',
 28  completed: 'var(--color-success)',
 29  failed: 'var(--color-error)',
 30  cancelled: 'var(--color-text-secondary)',
 31};
 32
 33/** Human-readable status labels. */
 34export const STATUS_LABELS: Record<PipelineStatus, string> = {
 35  running: 'RUNNING',
 36  queued: 'QUEUED',
 37  completed: 'DONE',
 38  failed: 'FAILED',
 39  cancelled: 'CANCELLED',
 40};
 41
 42/** Color mapping for agent event types in the output tab. */
 43const EVENT_TYPE_COLORS: Record<AgentEventType, string> = {
 44  thinking: 'var(--color-text-secondary)',
 45  action: '#39d353',
 46  output: 'var(--color-success)',
 47  signal: 'var(--color-warning)',
 48  error: 'var(--color-error)',
 49};
 50
 51/** AgentRunCard displays a pipeline run as a collapsible card with output, phases, and files tabs. */
 52export default function AgentRunCard({
 53  card,
 54  phases,
 55  agentTeam,
 56  agentEvents,
 57  onCancel,
 58  viewMode: _viewMode,
 59}: AgentRunCardProps): React.JSX.Element {
 60  const { run, issue, project } = card;
 61  const [expanded, setExpanded] = useState(false);
 62  const [activeTab, setActiveTab] = useState<DetailTab>('output');
 63  const [elapsed, setElapsed] = useState('');
 64  const outputRef = useRef<HTMLDivElement>(null);
 65
 66  // Live elapsed timer
 67  useEffect(() => {
 68    if (run.status !== 'running' && run.status !== 'queued') {
 69      if (run.started_at && run.completed_at) {
 70        const diff = new Date(run.completed_at).getTime() - new Date(run.started_at).getTime();
 71        const m = Math.floor(diff / 60000);
 72        const s = Math.floor((diff % 60000) / 1000);
 73        setElapsed(`${String(m).padStart(2, '0')}:${String(s).padStart(2, '0')}`);
 74      }
 75      return;
 76    }
 77
 78    const start = new Date(run.started_at).getTime();
 79    const tick = () => {
 80      const diff = Date.now() - start;
 81      const m = Math.floor(diff / 60000);
 82      const s = Math.floor((diff % 60000) / 1000);
 83      setElapsed(`${String(m).padStart(2, '0')}:${String(s).padStart(2, '0')}`);
 84    };
 85    tick();
 86    const interval = setInterval(tick, 1000);
 87    return () => clearInterval(interval);
 88  }, [run.status, run.started_at, run.completed_at]);
 89
 90  // Auto-scroll output
 91  useEffect(() => {
 92    if (expanded && activeTab === 'output' && outputRef.current) {
 93      outputRef.current.scrollTop = outputRef.current.scrollHeight;
 94    }
 95  });
 96
 97  // Collect all events for this run's agent team
 98  const allEvents: AgentEvent[] = [];
 99  if (agentTeam && agentEvents) {
100    for (const task of agentTeam.tasks) {
101      const events = agentEvents.get(task.id) ?? [];
102      allEvents.push(...events);
103    }
104    allEvents.sort((a, b) => new Date(a.created_at).getTime() - new Date(b.created_at).getTime());
105  }
106
107  const progress = run.phase_count
108    ? Math.round(((run.current_phase ?? 0) / run.phase_count) * 100)
109    : 0;
110
111  return (
112    <div
113      data-testid="agent-run-card"
114      onClick={() => setExpanded(!expanded)}
115      style={{
116        backgroundColor: 'var(--color-bg-card)',
117        border: '1px solid var(--color-border)',
118        borderLeft: `3px solid ${STATUS_DOT_COLORS[run.status] ?? 'var(--color-border)'}`,
119        cursor: 'pointer',
120        transition: 'background-color 0.15s',
121      }}
122      onMouseEnter={e => (e.currentTarget.style.backgroundColor = 'var(--color-bg-card-hover)')}
123      onMouseLeave={e => (e.currentTarget.style.backgroundColor = 'var(--color-bg-card)')}
124    >
125      {/* Collapsed view */}
126      <div style={{
127        display: 'flex',
128        alignItems: 'center',
129        padding: '12px',
130        gap: '12px',
131      }}>
132        {/* Status dot */}
133        <span
134          data-testid="status-dot"
135          className={run.status === 'running' ? 'pulse-dot' : undefined}
136          style={{
137            width: '8px',
138            height: '8px',
139            borderRadius: '50%',
140            backgroundColor: STATUS_DOT_COLORS[run.status],
141            flexShrink: 0,
142          }}
143        />
144
145        {/* Project badge + title */}
146        <div style={{ flex: 1, minWidth: 0 }}>
147          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
148            <span style={{
149              fontSize: '10px',
150              padding: '1px 6px',
151              backgroundColor: 'var(--color-border)',
152              color: 'var(--color-text-secondary)',
153              textTransform: 'uppercase',
154              letterSpacing: '0.5px',
155              flexShrink: 0,
156            }}>
157              {project.name}
158            </span>
159            <span style={{
160              overflow: 'hidden',
161              textOverflow: 'ellipsis',
162              whiteSpace: 'nowrap',
163              fontSize: '13px',
164            }}>
165              {issue.title}
166            </span>
167          </div>
168        </div>
169
170        {/* Phase dots */}
171        {run.phase_count && (
172          <div style={{ display: 'flex', gap: '3px', flexShrink: 0 }}>
173            {Array.from({ length: run.phase_count }, (_, i) => {
174              const phaseNum = i + 1;
175              const isCurrent = phaseNum === (run.current_phase ?? 0);
176              const isDone = phaseNum < (run.current_phase ?? 0);
177              return (
178                <span
179                  key={i}
180                  data-testid="phase-dot"
181                  className={isCurrent && run.status === 'running' ? 'pulse-dot' : undefined}
182                  style={{
183                    width: '6px',
184                    height: '6px',
185                    borderRadius: '50%',
186                    backgroundColor: isDone
187                      ? 'var(--color-success)'
188                      : isCurrent
189                        ? 'var(--color-info)'
190                        : 'var(--color-border)',
191                  }}
192                />
193              );
194            })}
195          </div>
196        )}
197
198        {/* Status label */}
199        <span style={{
200          fontSize: '11px',
201          color: STATUS_DOT_COLORS[run.status],
202          fontWeight: 600,
203          flexShrink: 0,
204          width: '60px',
205          textAlign: 'right',
206        }}>
207          {STATUS_LABELS[run.status]}
208        </span>
209
210        {/* Elapsed */}
211        <span style={{
212          fontSize: '12px',
213          color: 'var(--color-text-secondary)',
214          flexShrink: 0,
215          width: '50px',
216          textAlign: 'right',
217          fontVariantNumeric: 'tabular-nums',
218        }}>
219          {elapsed}
220        </span>
221
222        {/* Expand chevron */}
223        <span style={{
224          color: 'var(--color-text-secondary)',
225          transform: expanded ? 'rotate(180deg)' : 'rotate(0deg)',
226          transition: 'transform 0.2s',
227          flexShrink: 0,
228        }}>
229          &#x25BC;
230        </span>
231      </div>
232
233      {/* Progress bar */}
234      {run.status === 'running' && (
235        <div style={{
236          height: '2px',
237          backgroundColor: 'var(--color-border)',
238        }}>
239          <div
240            data-testid="progress-bar"
241            style={{
242              height: '100%',
243              width: `${progress}%`,
244              backgroundColor: 'var(--color-success)',
245              transition: 'width 0.3s',
246            }}
247          />
248        </div>
249      )}
250
251      {/* Expanded detail view */}
252      {expanded && (
253        <div
254          onClick={e => e.stopPropagation()}
255          style={{ borderTop: '1px solid var(--color-border)' }}
256        >
257          {/* Tabs */}
258          <div style={{
259            display: 'flex',
260            borderBottom: '1px solid var(--color-border)',
261          }}>
262            {(['output', 'phases', 'files'] as DetailTab[]).map(tab => (
263              <button
264                key={tab}
265                onClick={() => setActiveTab(tab)}
266                style={{
267                  padding: '8px 16px',
268                  background: 'transparent',
269                  border: 'none',
270                  borderBottom: activeTab === tab ? '2px solid var(--color-success)' : '2px solid transparent',
271                  color: activeTab === tab ? 'var(--color-text-primary)' : 'var(--color-text-secondary)',
272                  cursor: 'pointer',
273                  fontSize: '12px',
274                  fontFamily: 'inherit',
275                  textTransform: 'uppercase',
276                  letterSpacing: '0.5px',
277                }}
278              >
279                {tab}
280              </button>
281            ))}
282
283            {/* Cancel button for running pipelines */}
284            {run.status === 'running' && onCancel && (
285              <button
286                onClick={() => onCancel(run.id)}
287                style={{
288                  marginLeft: 'auto',
289                  padding: '8px 16px',
290                  background: 'transparent',
291                  border: 'none',
292                  color: 'var(--color-error)',
293                  cursor: 'pointer',
294                  fontSize: '12px',
295                  fontFamily: 'inherit',
296                }}
297              >
298                cancel
299              </button>
300            )}
301          </div>
302
303          {/* Tab content */}
304          <div style={{ padding: '12px', maxHeight: '400px', overflow: 'hidden' }}>
305            {activeTab === 'output' && (
306              <div
307                ref={outputRef}
308                style={{
309                  backgroundColor: '#000',
310                  padding: '12px',
311                  maxHeight: '376px',
312                  overflowY: 'auto',
313                  fontSize: '12px',
314                  lineHeight: '1.6',
315                }}
316              >
317                {allEvents.length === 0 ? (
318                  <span style={{ color: 'var(--color-text-secondary)' }}>
319                    {run.status === 'queued' ? 'Waiting to start...' : 'No output yet...'}
320                  </span>
321                ) : (
322                  allEvents.map((event, i) => (
323                    <div key={i} style={{ color: EVENT_TYPE_COLORS[event.event_type] ?? 'var(--color-text-primary)' }}>
324                      <span style={{ color: 'var(--color-text-secondary)', marginRight: '8px' }}>
325                        [{event.event_type}]
326                      </span>
327                      {event.content}
328                    </div>
329                  ))
330                )}
331              </div>
332            )}
333
334            {activeTab === 'phases' && (
335              <div style={{ fontSize: '13px' }}>
336                {(phases ?? []).length === 0 ? (
337                  <span style={{ color: 'var(--color-text-secondary)' }}>No phases yet...</span>
338                ) : (
339                  (phases ?? []).map((phase, i) => (
340                    <div key={i} style={{
341                      display: 'flex',
342                      alignItems: 'center',
343                      gap: '12px',
344                      padding: '6px 0',
345                      borderBottom: '1px solid var(--color-border)',
346                    }}>
347                      <span style={{
348                        width: '16px',
349                        textAlign: 'center',
350                        color: phase.status === 'completed'
351                          ? 'var(--color-success)'
352                          : phase.status === 'running'
353                            ? 'var(--color-info)'
354                            : 'var(--color-text-secondary)',
355                      }}>
356                        {phase.status === 'completed' ? '\u2713' : phase.status === 'running' ? '\u25B6' : '\u25CB'}
357                      </span>
358                      <span style={{ flex: 1 }}>{phase.phase_name}</span>
359                      {phase.iteration != null && phase.budget != null && (
360                        <span style={{ color: 'var(--color-text-secondary)', fontSize: '11px' }}>
361                          iter {phase.iteration}/{phase.budget}
362                        </span>
363                      )}
364                      {phase.review_status && (
365                        <span style={{
366                          fontSize: '10px',
367                          padding: '1px 6px',
368                          backgroundColor: phase.review_status === 'passed'
369                            ? 'var(--color-success)'
370                            : 'var(--color-warning)',
371                          color: '#000',
372                        }}>
373                          {phase.review_status}
374                        </span>
375                      )}
376                    </div>
377                  ))
378                )}
379              </div>
380            )}
381
382            {activeTab === 'files' && (
383              <div style={{ fontSize: '13px', color: 'var(--color-text-secondary)' }}>
384                {run.branch_name ? (
385                  <div style={{ marginBottom: '8px' }}>
386                    <span style={{ color: 'var(--color-text-secondary)' }}>branch: </span>
387                    <span style={{ color: 'var(--color-info)' }}>{run.branch_name}</span>
388                  </div>
389                ) : null}
390                {run.pr_url ? (
391                  <div>
392                    <span style={{ color: 'var(--color-text-secondary)' }}>PR: </span>
393                    <a
394                      href={run.pr_url}
395                      target="_blank"
396                      rel="noopener noreferrer"
397                      style={{ color: 'var(--color-info)' }}
398                      onClick={e => e.stopPropagation()}
399                    >
400                      {run.pr_url}
401                    </a>
402                  </div>
403                ) : null}
404                {!run.branch_name && !run.pr_url && (
405                  <span>No file changes yet...</span>
406                )}
407              </div>
408            )}
409          </div>
410        </div>
411      )}
412    </div>
413  );
414}
