  1export interface Project {
  2  id: number;
  3  name: string;
  4  path: string;
  5  github_repo: string | null;
  6  created_at: string;
  7}
  8
  9export type IssueColumn = 'backlog' | 'ready' | 'in_progress' | 'in_review' | 'done';
 10export type Priority = 'low' | 'medium' | 'high' | 'critical';
 11export type PipelineStatus = 'queued' | 'running' | 'completed' | 'failed' | 'cancelled';
 12export type AgentTaskStatus = 'pending' | 'running' | 'completed' | 'failed' | 'cancelled';
 13export type AgentRole = 'planner' | 'coder' | 'tester' | 'reviewer' | 'browser_verifier' | 'test_verifier';
 14export type AgentEventType = 'thinking' | 'action' | 'output' | 'signal' | 'error';
 15export type ExecutionStrategy = 'parallel' | 'sequential' | 'wave_pipeline' | 'adaptive';
 16export type IsolationStrategy = 'worktree' | 'container' | 'hybrid' | 'shared';
 17export type SignalType = 'progress' | 'blocker' | 'pivot';
 18export type VerificationType = 'browser' | 'test_build';
 19
 20export interface Issue {
 21  id: number;
 22  project_id: number;
 23  title: string;
 24  description: string;
 25  column: IssueColumn;
 26  position: number;
 27  priority: Priority;
 28  labels: string[];
 29  github_issue_number: number | null;
 30  created_at: string;
 31  updated_at: string;
 32}
 33
 34export interface PipelineRun {
 35  id: number;
 36  issue_id: number;
 37  status: PipelineStatus;
 38  phase_count: number | null;
 39  current_phase: number | null;
 40  iteration: number | null;
 41  summary: string | null;
 42  error: string | null;
 43  branch_name: string | null;
 44  pr_url: string | null;
 45  team_id: number | null;
 46  has_team: boolean;
 47  started_at: string;
 48  completed_at: string | null;
 49}
 50
 51export interface BoardView {
 52  project: Project;
 53  columns: ColumnView[];
 54}
 55
 56export interface ColumnView {
 57  name: IssueColumn;
 58  issues: IssueWithStatus[];
 59}
 60
 61export interface IssueWithStatus {
 62  issue: Issue;
 63  active_run: PipelineRun | null;
 64}
 65
 66export interface PipelinePhase {
 67  id: number;
 68  run_id: number;
 69  phase_number: string;
 70  phase_name: string;
