              key={project.id}
              className="project-sidebar-row"
              style={{
                display: 'flex',
                alignItems: 'center',
                position: 'relative',
              }}
            >
              <button
                onClick={() => onSelectProject(project.id)}
                style={{
                  width: '100%',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px 12px',
                  paddingRight: '28px',
                  background: isSelected ? 'var(--color-bg-card-hover)' : 'transparent',
                  border: 'none',
                  borderLeft: isSelected ? '2px solid var(--color-success)' : '2px solid transparent',
                  color: 'var(--color-text-primary)',
                  cursor: 'pointer',
                  fontSize: '13px',
                  textAlign: 'left',
                  fontFamily: 'inherit',
                }}
              >
                {/* Status dot */}
                <span
                  className={hasActive ? 'pulse-dot' : undefined}
                  style={{
                    width: '6px',
                    height: '6px',
                    borderRadius: '50%',
                    backgroundColor: hasActive ? 'var(--color-success)' : 'var(--color-text-secondary)',
                    flexShrink: 0,
                  }}
                />
                {/* Name */}
                <span style={{
                  flex: 1,
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                }}>
                  {project.name}
                </span>
                {/* Run count badge */}
                {hasActive && (
                  <span style={{
                    fontSize: '11px',
                    padding: '1px 6px',
                    backgroundColor: 'var(--color-border)',
                    borderRadius: '8px',
                    color: 'var(--color-success)',
                  }}>
                    {stats.running}
                  </span>
                )}
              </button>
              {/* Delete button  visible on row hover */}
              <button
                className="project-delete-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  if (window.confirm(`Delete project "${project.name}"? This will remove all its issues and pipeline runs.`)) {
                    onDeleteProject(project.id);
                  }
                }}
                style={{
                  position: 'absolute',
                  right: '8px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  color: 'var(--color-text-secondary)',
                  cursor: 'pointer',
                  fontSize: '14px',
                  lineHeight: 1,
                  padding: '2px 4px',
                  borderRadius: '3px',
                  opacity: 0,
                  transition: 'opacity 0.15s, color 0.15s',
                }}
                title="Delete project"
              >
                x
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

/** Sidebar listing all projects with status indicators and filtering. */
import type { Project } from '../types';

/**
 * Props for the ProjectSidebar component.
 * @property projects - List of all projects to display
 * @property selectedProjectId - ID of the currently selected project, or null for "All Projects"
 * @property onSelectProject - Callback when a project is selected; null means "All Projects"
 * @property onDeleteProject - Callback when a project delete is requested
 * @property runsByProject - Map of project ID to running/total run counts
 */
export interface ProjectSidebarProps {
  projects: Project[];
  selectedProjectId: number | null;
  onSelectProject: (projectId: number | null) => void;
  onDeleteProject: (projectId: number) => void;
  runsByProject: Map<number, { running: number; total: number }>;
}

/** Sidebar component that displays a list of projects with status dots and run count badges. */
export default function ProjectSidebar({
  projects,
  selectedProjectId,
  onSelectProject,
  onDeleteProject,
  runsByProject,
}: ProjectSidebarProps): React.JSX.Element {
  return (
    <div style={{
      width: '200px',
      backgroundColor: 'var(--color-bg-card)',
      borderRight: '1px solid var(--color-border)',
      display: 'flex',
      flexDirection: 'column',
      overflow: 'hidden',
      flexShrink: 0,
    }}>
      {/* Header */}
      <div style={{
        padding: '12px',
        borderBottom: '1px solid var(--color-border)',
        fontSize: '11px',
        color: 'var(--color-text-secondary)',
        textTransform: 'uppercase',
        letterSpacing: '1px',
      }}>
        Projects
      </div>

      {/* Project list */}
      <div style={{ flex: 1, overflowY: 'auto', padding: '4px 0' }}>
        {/* All Projects */}
        <button
          onClick={() => onSelectProject(null)}
          style={{
            width: '100%',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            padding: '8px 12px',
            background: selectedProjectId === null ? 'var(--color-bg-card-hover)' : 'transparent',
            border: 'none',
            borderLeft: selectedProjectId === null ? '2px solid var(--color-success)' : '2px solid transparent',
            color: 'var(--color-text-primary)',
            cursor: 'pointer',
            fontSize: '13px',
            textAlign: 'left',
            fontFamily: 'inherit',
          }}
        >
          All Projects
        </button>

        {projects.map(project => {
          const stats = runsByProject.get(project.id);
          const hasActive = stats !== undefined && stats.running > 0;
          const isSelected = selectedProjectId === project.id;

          return (
            <div
              key={project.id}
              className="project-sidebar-row"
              style={{
                display: 'flex',
                alignItems: 'center',
                position: 'relative',
              }}
            >
              <button
                onClick={() => onSelectProject(project.id)}
                style={{
                  width: '100%',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px 12px',
                  paddingRight: '28px',
                  background: isSelected ? 'var(--color-bg-card-hover)' : 'transparent',
                  border: 'none',
                  borderLeft: isSelected ? '2px solid var(--color-success)' : '2px solid transparent',
                  color: 'var(--color-text-primary)',
                  cursor: 'pointer',
                  fontSize: '13px',
                  textAlign: 'left',
                  fontFamily: 'inherit',
                }}
              >
                {/* Status dot */}
                <span
                  className={hasActive ? 'pulse-dot' : undefined}
                  style={{
                    width: '6px',
                    height: '6px',
                    borderRadius: '50%',
                    backgroundColor: hasActive ? 'var(--color-success)' : 'var(--color-text-secondary)',
                    flexShrink: 0,
                  }}
                />
                {/* Name */}
                <span style={{
                  flex: 1,
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                }}>
                  {project.name}
                </span>
                {/* Run count badge */}
                {hasActive && (
                  <span style={{
                    fontSize: '11px',
                    padding: '1px 6px',
                    backgroundColor: 'var(--color-border)',
                    borderRadius: '8px',
                    color: 'var(--color-success)',
                  }}>
                    {stats.running}
                  </span>
                )}
              </button>
              {/* Delete button  visible on row hover */}
              <button
                className="project-delete-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  onDeleteProject(project.id, project.name);
                }}
                style={{
                  position: 'absolute',
                  right: '8px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  color: 'var(--color-text-secondary)',
                  cursor: 'pointer',
                  fontSize: '14px',
                  lineHeight: 1,
                  padding: '2px 4px',
                  borderRadius: '3px',
                  opacity: 0,
                  transition: 'opacity 0.15s, color 0.15s',
                }}
                title="Delete project"
              >
                x
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

import { useState, useCallback, useMemo } from 'react';
import type { ViewMode, Project } from './types';
import useMissionControl from './hooks/useMissionControl';
import { WebSocketProvider } from './contexts/WebSocketContext';
import StatusBar from './components/StatusBar';
import ProjectSidebar from './components/ProjectSidebar';
import AgentRunCard from './components/AgentRunCard';
import EventLog from './components/EventLog';
import FloatingActionButton from './components/FloatingActionButton';
import NewIssueModal from './components/NewIssueModal';
import ConfirmDialog from './components/ConfirmDialog';
import { ProjectSetup } from './components/ProjectSetup';
import { api } from './api/client';

/**
 * Mission Control dashboard  the main application shell.
 * Aggregates all projects and agent runs into a unified monitoring view.
 * Layout: StatusBar (top) -> flex row [ProjectSidebar | Agent Grid] -> EventLog (bottom).
 */
function MissionControl() {
  const {
    projects,
    agentRunCards,
    statusCounts,
    eventLog,
    phases,
    agentTeams,
    agentEvents,
    loading,
    selectedProjectId,
    setSelectedProjectId,
    triggerPipeline,
    cancelPipeline,
    createIssue,
    createProject,
    deleteProject,
    issuesByProject,
  } = useMissionControl();

  const [viewMode, setViewMode] = useState<ViewMode>('grid');
  const [showNewIssueModal, setShowNewIssueModal] = useState(false);
  const [newIssueProjectId, setNewIssueProjectId] = useState<number | null>(null);
  const [showProjectSetup, setShowProjectSetup] = useState(false);
  const [deleteConfirm, setDeleteConfirm] = useState<{ id: number; name: string } | null>(null);

  /** Compute run counts by project for the sidebar */
  const runsByProject = useMemo(() => {
    const map = new Map<number, { running: number; total: number }>();
    for (const card of agentRunCards) {
      const pid = card.project.id;
      const existing = map.get(pid) ?? { running: 0, total: 0 };
      existing.total++;
      if (card.run.status === 'running') existing.running++;
      map.set(pid, existing);
    }
    return map;
  }, [agentRunCards]);

  /** Projects filtered by the current sidebar selection */
  const displayedProjects = useMemo(() => {
    if (selectedProjectId === null) return projects;
    return projects.filter(p => p.id === selectedProjectId);
  }, [projects, selectedProjectId]);

  const handleNewIssue = useCallback(() => {
    setNewIssueProjectId(null);
    setShowNewIssueModal(true);
  }, []);

  const handleNewProject = useCallback(() => {
    setShowProjectSetup(true);
  }, []);

  const handleSyncGithub = useCallback(async () => {
    const project = projects.find(p =>
      selectedProjectId === null ? true : p.id === selectedProjectId
    );
    if (project) {
      try {
        await api.syncGithub(project.id);
      } catch (err) {
        console.error('Sync failed:', err);
      }
    }
  }, [projects, selectedProjectId]);

  const handleIssueSubmit = useCallback(async (projectId: number, title: string, description: string) => {
    const issue = await createIssue(projectId, title, description);
    await triggerPipeline(issue.id);
  }, [createIssue, triggerPipeline]);

  const handleProjectSelect = useCallback((project: Project) => {
    setSelectedProjectId(project.id);
    setShowProjectSetup(false);
  }, [setSelectedProjectId]);

  const handleProjectCreate = useCallback((name: string, path: string) => {
    createProject(name, path).then(() => {
      setShowProjectSetup(false);
    }).catch(console.error);
  }, [createProject]);

  const handleCloneProject = useCallback(async (repoUrl: string) => {
    const project = await api.cloneProject(repoUrl);
    setShowProjectSetup(false);
    setSelectedProjectId(project.id);
  }, [setSelectedProjectId]);

  const handleDeleteProject = useCallback(async (projectId: number) => {
    await deleteProject(projectId);
    if (selectedProjectId === projectId) {
      setSelectedProjectId(null);
    }
  }, [deleteProject, selectedProjectId, setSelectedProjectId]);

  // Loading state
  if (loading) {
    return (
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        backgroundColor: 'var(--color-bg-primary)',
        color: 'var(--color-text-primary)',
        gap: '12px',
        fontSize: '14px',
      }}>
        <span className="pulse-dot" style={{
          width: '8px',
          height: '8px',
          borderRadius: '50%',
          backgroundColor: 'var(--color-success)',
        }} />
        Initializing Mission Control...
      </div>
    );
  }

  // No projects  show ProjectSetup full-screen
  if (projects.length === 0) {
    return (
      <div style={{
        height: '100vh',
        backgroundColor: 'var(--color-bg-primary)',
      }}>
        <ProjectSetup
          projects={[]}
          onSelect={handleProjectSelect}
          onCreate={handleProjectCreate}
          onClone={handleCloneProject}
        />
      </div>
    );
  }

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      backgroundColor: 'var(--color-bg-primary)',
      color: 'var(--color-text-primary)',
    }}>
      {/* Top bar */}
      <StatusBar
        agentCounts={{
          running: statusCounts.running,
          queued: statusCounts.queued,
          completed: statusCounts.completed,
          failed: statusCounts.failed,
        }}
        projectCount={projects.length}
        viewMode={viewMode}
        onViewModeChange={setViewMode}
      />

      {/* Main content: sidebar + agent grid */}
      <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
        <ProjectSidebar
          projects={projects}
          selectedProjectId={selectedProjectId}
          onSelectProject={setSelectedProjectId}
          onDeleteProject={handleDeleteProject}
          runsByProject={runsByProject}
        />

        {/* Agent run grid */}
        <div style={{
          flex: 1,
          overflowY: 'auto',
          padding: '16px',
        }}>
          {agentRunCards.length === 0 ? (
            <div style={{
              display: viewMode === 'grid' ? 'grid' : 'flex',
              gridTemplateColumns: viewMode === 'grid' ? 'repeat(auto-fill, minmax(400px, 1fr))' : undefined,
              flexDirection: viewMode === 'list' ? 'column' : undefined,
              gap: '12px',
            }}>
              {displayedProjects.map(project => {
                const issueCount = issuesByProject.get(project.id) ?? 0;
                return (
                  <div
                    key={project.id}
                    style={{
                      backgroundColor: 'var(--color-bg-card)',
                      border: '1px solid var(--color-border)',
                      borderLeft: '3px solid var(--color-text-secondary)',
                    }}
                  >
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      padding: '12px',
                      gap: '12px',
                    }}>
                      {/* Idle dot */}
                      <span style={{
                        width: '8px',
                        height: '8px',
                        borderRadius: '50%',
                        backgroundColor: 'var(--color-text-secondary)',
                        flexShrink: 0,
                      }} />

                      {/* Project name */}
                      <div style={{ flex: 1, minWidth: 0 }}>
                        <div style={{
                          fontSize: '13px',
                          fontWeight: 600,
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap',
                        }}>
                          {project.name}
                        </div>
                        <div style={{
                          fontSize: '11px',
                          color: 'var(--color-text-secondary)',
                          marginTop: '2px',
                        }}>
                          {issueCount} {issueCount === 1 ? 'issue' : 'issues'}
                        </div>
                      </div>

                      {/* Status label */}
                      <span style={{
                        fontSize: '11px',
                        color: 'var(--color-text-secondary)',
                        fontWeight: 600,
                        flexShrink: 0,
                      }}>
                        IDLE
                      </span>

                      {/* Create Issue button */}
                      <button
                        onClick={() => {
                          setNewIssueProjectId(project.id);
                          setShowNewIssueModal(true);
                        }}
                        style={{
                          padding: '4px 10px',
                          fontSize: '11px',
                          fontFamily: 'inherit',
                          background: 'transparent',
                          border: '1px solid var(--color-border)',
                          color: 'var(--color-success)',
                          cursor: 'pointer',
                          flexShrink: 0,
                        }}
                        onMouseEnter={e => (e.currentTarget.style.backgroundColor = 'var(--color-bg-card-hover)')}
                        onMouseLeave={e => (e.currentTarget.style.backgroundColor = 'transparent')}
                      >
                        + Issue
                      </button>

                      {/* Delete project button */}
                      <button
                        onClick={() => setDeleteConfirm({ id: project.id, name: project.name })}
                        style={{
                          padding: '4px 8px',
                          fontSize: '11px',
                          fontFamily: 'inherit',
                          background: 'transparent',
                          border: '1px solid var(--color-border)',
                          color: 'var(--color-text-secondary)',
                          cursor: 'pointer',
                          flexShrink: 0,
                        }}
                        onMouseEnter={e => {
                          e.currentTarget.style.backgroundColor = 'var(--color-bg-card-hover)';
                          e.currentTarget.style.color = 'var(--color-error)';
                          e.currentTarget.style.borderColor = 'var(--color-error)';
                        }}
                        onMouseLeave={e => {
                          e.currentTarget.style.backgroundColor = 'transparent';
                          e.currentTarget.style.color = 'var(--color-text-secondary)';
                          e.currentTarget.style.borderColor = 'var(--color-border)';
                        }}
                        title="Delete project"
                      >

                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <div style={{
              display: viewMode === 'grid' ? 'grid' : 'flex',
              gridTemplateColumns: viewMode === 'grid' ? 'repeat(auto-fill, minmax(400px, 1fr))' : undefined,
              flexDirection: viewMode === 'list' ? 'column' : undefined,
              gap: '12px',
            }}>
              {agentRunCards.map(card => (
                <AgentRunCard
                  key={card.run.id}
                  card={card}
                  phases={phases.get(card.run.id)}
                  agentTeam={agentTeams.get(card.run.id)}
                  agentEvents={agentEvents}
                  onCancel={cancelPipeline}
                  viewMode={viewMode}
                />
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Bottom event log */}
      <EventLog entries={eventLog} />

      {/* Floating action button */}
      <FloatingActionButton
        onNewIssue={handleNewIssue}
        onNewProject={handleNewProject}
        onSyncGithub={handleSyncGithub}
      />

      {/* New Issue Modal */}
      {showNewIssueModal && (
        <NewIssueModal
          projects={projects}
          defaultProjectId={newIssueProjectId}
          onSubmit={handleIssueSubmit}
          onClose={() => { setShowNewIssueModal(false); setNewIssueProjectId(null); }}
        />
      )}

      {/* Delete Confirmation Dialog */}
      {deleteConfirm && (
        <ConfirmDialog
          title="Delete project"
          message={`Delete "${deleteConfirm.name}"? This will remove all its issues and pipeline runs.`}
          confirmLabel="Delete"
          onConfirm={() => {
            handleDeleteProject(deleteConfirm.id);
            setDeleteConfirm(null);
          }}
          onCancel={() => setDeleteConfirm(null)}
        />
      )}

      {/* Project Setup Modal */}
      {showProjectSetup && (
        <div
          data-testid="project-setup-modal"
          onClick={() => setShowProjectSetup(false)}
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.7)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 100,
          }}
        >
          <div onClick={e => e.stopPropagation()} style={{ width: '100%', maxWidth: '500px' }}>
            <ProjectSetup
              projects={projects}
              onSelect={handleProjectSelect}
              onCreate={handleProjectCreate}
              onClone={handleCloneProject}
            />
          </div>
        </div>
      )}
    </div>
  );
}

function App() {
  const wsUrl = `${window.location.protocol === 'https:' ? 'wss:' : 'ws:'}//${window.location.host}/ws`;
  return (
    <WebSocketProvider url={wsUrl}>
      <MissionControl />
    </WebSocketProvider>
  );
}

export default App;

