    //  Agent Events 

    pub fn create_agent_event(
        &self,
        task_id: i64,
        event_type: &str,
        content: &str,
        metadata: Option<&serde_json::Value>,
    ) -> Result<AgentEvent> {
        let metadata_str = match metadata {
            Some(m) => {
                Some(serde_json::to_string(m).context("Failed to serialize event metadata")?)
            }
            None => None,
        };
        self.conn.execute(
            "INSERT INTO agent_events (task_id, event_type, content, metadata) VALUES (?1, ?2, ?3, ?4)",
            params![task_id, event_type, content, metadata_str],
        ).context("Failed to insert agent event")?;
        let id = self.conn.last_insert_rowid();
        Ok(AgentEvent {
            id,
            task_id,
            event_type: {
                let val = event_type;
                val.parse()
                    .map_err(|_| anyhow::anyhow!("invalid event_type in database: '{}'", val))?
            },
            content: content.to_string(),
            metadata: metadata.cloned(),
            created_at: chrono::Utc::now().to_rfc3339(),
        })
    }

    pub fn get_agent_events(
        &self,
        task_id: i64,
        limit: i64,
        offset: i64,
    ) -> Result<Vec<AgentEvent>> {
        let mut stmt = self
            .conn
            .prepare(
                "SELECT id, task_id, event_type, content, metadata, created_at \
             FROM agent_events WHERE task_id = ?1 ORDER BY id DESC LIMIT ?2 OFFSET ?3",
            )
            .context("Failed to prepare get_agent_events")?;
        let rows = stmt.query_map(params![task_id, limit, offset], |row| {
            Ok((
                row.get::<_, i64>(0)?,
                row.get::<_, i64>(1)?,
                row.get::<_, String>(2)?,
                row.get::<_, String>(3)?,
                row.get::<_, Option<String>>(4)?,
                row.get::<_, String>(5)?,
            ))
        })?;
        let mut events = Vec::new();
        for row in rows {
            let (id, task_id, event_type_str, content, metadata_str, created_at) = row?;
            let metadata: Option<serde_json::Value> =
                match metadata_str {
                    Some(s) => Some(serde_json::from_str(&s).map_err(|e| {
                        anyhow::anyhow!("corrupt event metadata JSON '{}': {}", s, e)
                    })?),
                    None => None,
                };
            events.push(AgentEvent {
                id,
                task_id,
                event_type: event_type_str.parse().map_err(|_| {
                    anyhow::anyhow!("invalid event_type in database: '{}'", event_type_str)
                })?,
                content,
                metadata,
                created_at,
            });
        }
        Ok(events)
    }

